function cardResize(){document.body.clientWidth>"1100"&&document.querySelector(".card-container").setAttribute("style","display:grid; grid-template-columns: repeat(4,1fr);"),document.body.clientWidth<="1100"&&document.body.clientWidth>="540"&&document.querySelector(".card-container").setAttribute("style","display:grid; grid-template-columns: repeat(2,1fr);"),document.body.clientWidth<"540"&&document.querySelector(".card-container").setAttribute("style","display:grid; grid-template-columns: repeat(1,1fr);")}document.addEventListener("DOMContentLoaded",(function(event){document.querySelectorAll(".card-container").length>0&&(cardResize(),window.addEventListener("resize",cardResize))}));